package day4;

public class singledimensionArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int [] i = { 50,60,70,80};
		String [] s= { "rama", "hari", "nama"};
		
		for (int a : i) {
			System.out.println(a);
			
		}
		
		for (String l : s) {
			System.out.println(l);
		}
		

	}

}
